PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE title_pool (
      title_id TEXT PRIMARY KEY,
      category TEXT NOT NULL,
      title TEXT NOT NULL,
      score INTEGER NOT NULL,
      validated INTEGER DEFAULT 0,
      used INTEGER DEFAULT 0,
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP, ai_model TEXT DEFAULT 'claude',
      UNIQUE(category, title)
    );
CREATE TABLE coupang_crawl_queue (queue_id TEXT PRIMARY KEY, user_id TEXT NOT NULL, product_url TEXT NOT NULL, status TEXT DEFAULT 'pending', retry_count INTEGER DEFAULT 0, max_retries INTEGER DEFAULT 3, timeout_seconds INTEGER DEFAULT 60, error_message TEXT, product_info TEXT, custom_category TEXT, created_at TEXT DEFAULT (datetime('now')), updated_at TEXT DEFAULT (datetime('now')), processed_at TEXT, destination TEXT DEFAULT 'my_list', source_url TEXT);
CREATE TABLE IF NOT EXISTS "user" (
  user_id TEXT PRIMARY KEY,
  email TEXT UNIQUE NOT NULL,
  password TEXT NOT NULL,
  nickname TEXT,
  is_admin INTEGER DEFAULT 0,
  credits INTEGER DEFAULT 0,
  is_email_verified INTEGER DEFAULT 0,
  verification_token TEXT,
  memo TEXT,
  google_sites_url TEXT,
  google_sites_edit_url TEXT,
  google_sites_home_url TEXT,
  created_at TEXT DEFAULT (datetime('now')),
  updated_at TEXT DEFAULT (datetime('now'))
);
CREATE TABLE IF NOT EXISTS "task_lock" (
  task_type TEXT PRIMARY KEY CHECK(task_type IN ('script', 'image', 'video', 'youtube')),
  locked_by TEXT,
  locked_at TEXT,
  worker_pid INTEGER
);
CREATE TABLE IF NOT EXISTS "automation_setting" (
  key TEXT PRIMARY KEY,
  value TEXT NOT NULL,
  description TEXT,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
);
CREATE TABLE IF NOT EXISTS "automation_log" (
  log_id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  channel_id TEXT NOT NULL,
  channel_name TEXT NOT NULL,
  category TEXT NOT NULL,
  status TEXT DEFAULT 'started' CHECK(status IN ('started', 'fetching', 'generating', 'evaluating', 'completed', 'failed')),
  step TEXT,
  models_used TEXT,
  titles_generated TEXT,
  best_title TEXT,
  best_score REAL,
  result_title_id TEXT,
  product_info TEXT,
  error_message TEXT,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  completed_at DATETIME
, title_id TEXT, level TEXT DEFAULT 'info', details TEXT);
CREATE TABLE IF NOT EXISTS "youtube_channel_setting" (
  setting_id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  channel_id TEXT NOT NULL,
  channel_name TEXT NOT NULL,
  color TEXT DEFAULT '#3b82f6',
  posting_mode TEXT DEFAULT 'fixed_interval' CHECK(posting_mode IN ('fixed_interval', 'weekday_time')),
  interval_value INTEGER,
  interval_unit TEXT CHECK(interval_unit IN ('hours', 'days')),
  weekday_times TEXT,
  categories TEXT,
  is_active INTEGER DEFAULT 1,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP, posting_times TEXT, default_time TEXT DEFAULT '11:00',
  UNIQUE(user_id, channel_id)
);
CREATE TABLE user_content_category (
  category_id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  name TEXT NOT NULL,
  description TEXT,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  UNIQUE(user_id, name)
);
CREATE TABLE IF NOT EXISTS "coupang_product" (coupang_id TEXT PRIMARY KEY, user_id TEXT NOT NULL, product_url TEXT NOT NULL, deep_link TEXT, title TEXT NOT NULL, description TEXT, category TEXT NOT NULL, original_price REAL, discount_price REAL, image_url TEXT, status TEXT DEFAULT 'active', view_count INTEGER DEFAULT 0, click_count INTEGER DEFAULT 0, is_favorite INTEGER DEFAULT 0, created_at TEXT DEFAULT (datetime('now')), updated_at TEXT DEFAULT (datetime('now')), queue_id TEXT, FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS "user_session" (session_id TEXT PRIMARY KEY, user_id TEXT NOT NULL, expires_at TEXT NOT NULL, created_at TEXT DEFAULT (datetime('now')), FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS "user_activity_log" (activity_id INTEGER PRIMARY KEY AUTOINCREMENT, user_id TEXT NOT NULL, action TEXT NOT NULL, details TEXT, created_at TEXT DEFAULT (datetime('now')), FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS "user_credit_history" (history_id TEXT PRIMARY KEY, user_id TEXT NOT NULL, amount INTEGER NOT NULL, type TEXT NOT NULL, description TEXT, balance_after INTEGER NOT NULL, created_at TEXT DEFAULT (datetime('now')), FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS "user_charge_request" (request_id TEXT PRIMARY KEY, user_id TEXT NOT NULL, amount INTEGER NOT NULL, status TEXT DEFAULT 'pending', created_at TEXT DEFAULT (datetime('now')), processed_at TEXT, FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS "product_crawl_link" (link_id TEXT PRIMARY KEY, user_id TEXT NOT NULL, product_url TEXT NOT NULL, source_url TEXT NOT NULL, title TEXT, description TEXT, category TEXT, image_url TEXT, original_price REAL, discount_price REAL, created_at TEXT DEFAULT (datetime('now')), FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS "product_crawl_link_history" (history_id TEXT PRIMARY KEY, user_id TEXT NOT NULL, source_url TEXT NOT NULL, hostname TEXT, last_result_count INTEGER DEFAULT 0, last_duplicate_count INTEGER DEFAULT 0, last_error_count INTEGER DEFAULT 0, last_total_links INTEGER DEFAULT 0, last_status TEXT DEFAULT 'pending', last_message TEXT, last_job_id TEXT, last_crawled_at TEXT DEFAULT (datetime('now')), created_at TEXT DEFAULT (datetime('now')), updated_at TEXT DEFAULT (datetime('now')), FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS "product_crawl_link_pending" (pending_id TEXT PRIMARY KEY, user_id TEXT NOT NULL, product_url TEXT NOT NULL, source_url TEXT NOT NULL, title TEXT, status TEXT DEFAULT 'pending', created_at TEXT DEFAULT (datetime('now')), FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE);
CREATE TABLE user_social_media_account (
    id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL,
    platform TEXT NOT NULL,
    account_id TEXT NOT NULL,
    username TEXT,
    display_name TEXT,
    profile_picture TEXT,
    follower_count INTEGER,
    access_token TEXT,
    refresh_token TEXT,
    token_expires_at TEXT,
    is_default INTEGER DEFAULT 0,
    created_at TEXT DEFAULT CURRENT_TIMESTAMP,
    updated_at TEXT DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE
  );
CREATE TABLE user_category (
      id INTEGER PRIMARY KEY AUTOINCREMENT,
      user_id TEXT NOT NULL,
      category TEXT NOT NULL,
      display_name TEXT NOT NULL,
      keywords TEXT,
      description TEXT,
      is_default BOOLEAN DEFAULT 0,
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
      updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
      UNIQUE(user_id, category),
      FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE
    );
CREATE TABLE IF NOT EXISTS "task_schedule" (
      schedule_id TEXT PRIMARY KEY,
      task_id TEXT,  -- task 테이블 참조 (어떤 task를 실행할지)
      scheduled_time DATETIME NOT NULL,
      status TEXT DEFAULT 'pending' CHECK(status IN ('pending', 'processing', 'completed', 'failed', 'cancelled', 'waiting_for_upload')),
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
      updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
    , shortform_task_id TEXT, error_message TEXT, youtube_url TEXT);
CREATE TABLE IF NOT EXISTS "task" (
  task_id TEXT PRIMARY KEY,
  status TEXT DEFAULT 'draft' CHECK(status IN ('draft', 'active', 'completed', 'archived', 'cancelled')),
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP, user_id TEXT);
CREATE TABLE task_queue (
  task_id TEXT PRIMARY KEY,
  type TEXT NOT NULL CHECK(type IN ('schedule', 'script', 'image', 'video', 'youtube')),
  status TEXT NOT NULL CHECK(status IN ('waiting', 'processing', 'completed', 'failed', 'cancelled', 'timeout')),
  created_at TEXT NOT NULL,
  started_at TEXT,
  completed_at TEXT,
  user_id TEXT NOT NULL,
  error TEXT,
  elapsed_time INTEGER DEFAULT NULL
, script_completed_at TEXT, image_completed_at TEXT, video_completed_at TEXT, youtube_completed_at TEXT);
CREATE TABLE youtube_uploads (
        id TEXT PRIMARY KEY,
        content_id TEXT,
        task_id TEXT,
        channel_id TEXT,
        youtube_url TEXT NOT NULL,
        youtube_video_id TEXT,
        status TEXT DEFAULT 'active',
        uploaded_at TEXT DEFAULT CURRENT_TIMESTAMP,
        created_at TEXT DEFAULT CURRENT_TIMESTAMP
      );
CREATE TABLE IF NOT EXISTS "content" (
  content_id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  title TEXT NOT NULL,
  original_title TEXT,
  status TEXT DEFAULT 'pending',
  error TEXT,
  youtube_url TEXT,
  input_tokens INTEGER,
  output_tokens INTEGER,
  source_content_id TEXT,
  ai_model TEXT,
  prompt_format TEXT,
  product_info TEXT,
  category TEXT,
  created_at TEXT,
  updated_at TEXT
, converted_from_job_id TEXT, score INTEGER DEFAULT NULL, youtube_channel TEXT, youtube_publish_time DATETIME);
CREATE TABLE IF NOT EXISTS "content_setting" (
      content_id TEXT PRIMARY KEY,
      script_mode TEXT DEFAULT 'chrome',
      media_mode TEXT DEFAULT 'crawl',
      tts_voice TEXT DEFAULT 'ko-KR-SoonBokNeural',
      tts_speed TEXT DEFAULT '+0%',
      auto_create_shortform INTEGER DEFAULT 1,
      tags TEXT,
      settings TEXT,
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
      updated_at DATETIME DEFAULT CURRENT_TIMESTAMP, youtube_privacy TEXT DEFAULT 'public',
      FOREIGN KEY (content_id) REFERENCES content(content_id) ON DELETE CASCADE
    );
DELETE FROM sqlite_sequence;
CREATE INDEX idx_title_pool_category_score ON title_pool(category, score DESC, used ASC);
CREATE INDEX idx_coupang_crawl_queue_user_id ON coupang_crawl_queue(user_id);
CREATE INDEX idx_coupang_crawl_queue_status ON coupang_crawl_queue(status);
CREATE INDEX idx_automation_logs_user_status ON "automation_log"(user_id, status);
CREATE INDEX idx_automation_logs_created ON "automation_log"(created_at DESC);
CREATE INDEX idx_user_content_category_user_id ON user_content_category(user_id);
CREATE INDEX idx_title_pool_category ON title_pool(category);
CREATE INDEX idx_title_pool_used ON title_pool(used);
CREATE INDEX idx_title_pool_score ON title_pool(score);
CREATE INDEX idx_coupang_product_user_id ON coupang_product(user_id);
CREATE INDEX idx_user_session_user_id ON user_session(user_id);
CREATE INDEX idx_user_activity_log_user_id ON user_activity_log(user_id);
CREATE INDEX idx_user_credit_history_user_id ON user_credit_history(user_id);
CREATE INDEX idx_user_charge_request_user_id ON user_charge_request(user_id);
CREATE INDEX idx_product_crawl_link_user_id ON product_crawl_link(user_id);
CREATE INDEX idx_product_crawl_link_history_user_id ON product_crawl_link_history(user_id);
CREATE INDEX idx_product_crawl_link_pending_user_id ON product_crawl_link_pending(user_id);
CREATE INDEX idx_user_email ON user(email);
CREATE INDEX idx_user_verification_token ON user(verification_token);
CREATE INDEX idx_user_social_media_account_user_platform
    ON user_social_media_account(user_id, platform);
CREATE INDEX idx_task_queue_user_status ON task_queue(user_id, status);
CREATE INDEX idx_task_queue_completed_at ON task_queue(completed_at) WHERE status IN ('completed', 'failed', 'timeout');
CREATE INDEX idx_youtube_uploads_content ON youtube_uploads(content_id);
CREATE INDEX idx_youtube_uploads_task ON youtube_uploads(task_id);
CREATE INDEX idx_youtube_uploads_channel ON youtube_uploads(channel_id);
CREATE INDEX idx_youtube_uploads_video_id ON youtube_uploads(youtube_video_id);
CREATE INDEX idx_content_content_id ON content(content_id);
CREATE INDEX idx_content_user_id ON content(user_id);
CREATE INDEX idx_content_status ON content(status);
CREATE TRIGGER check_deep_link_length_insert
  BEFORE INSERT ON coupang_product
  BEGIN
    SELECT CASE WHEN LENGTH(NEW.deep_link) > 50 THEN RAISE(ABORT, 'deep_link must be 50 characters or less') END;
  END;
CREATE TRIGGER check_deep_link_length_update
  BEFORE UPDATE OF deep_link ON coupang_product
  BEGIN
    SELECT CASE WHEN LENGTH(NEW.deep_link) > 50 THEN RAISE(ABORT, 'deep_link must be 50 characters or less') END;
  END;
CREATE INDEX idx_task_user_id ON task(user_id);
CREATE INDEX idx_task_status ON task(status);
CREATE INDEX idx_task_queue_type_status
        ON task_queue(type, status, created_at ASC);
COMMIT;
